import React, {ChangeEvent, useState} from "react";
import { Button, Container, Input, useTheme} from "@mui/material";
import SendIcon from '@mui/icons-material/Send';
import {useMutation, useQuery} from "react-query";

export function TextBar() {

    const theme = useTheme();

    const [message, setMessage] = useState("");
    const [userName,setuserName] = useState("Admin");

    function useSendMessage(message, userName) {
        const { isLoading, data: responseData } = useQuery({
            queryFn: async () => {
                return fetch("http://localhost:8080/messages", {
                    method: "POST",
                    body: JSON.stringify({
                        message,
                        userName,
                    }),
                })
                    .then((response) => response.json());
            },
            queryKey: ["data"],
        });

        return { isLoading, data: responseData };
    }


    const sendMessage = () => {
        setMessage("");
        setuserName("");
    }

    const handleSubmit = (e: ChangeEvent<HTMLInputElement>) => {
        return setMessage(e.currentTarget.value);
    }


    return <div>
        <Container maxWidth="xl">
            <Input style={{width: "50%", height: "40px"}} onChange={handleSubmit}/>
            <Button variant="contained" onClick={sendMessage}>
                <SendIcon/>
            </Button>
        </Container>
    </div>
}
