import React from "react";
import {useQuery} from "react-query";

type messageType = {
    id: string,
    message: string,
    userName: string
}

export function MessageList(){

    const {data:messages,isLoading} = useQuery({
        // queryFn: ()=>fetch("http://localhost:8080/messages"),
        queryFn: async () => {
            const response = await fetch("localhost:8080/chat/messages");
            return response.json();
        },
        queryKey: ["data"]
    });

    if(isLoading)return <div>Loading...</div>

    return <div>
        <ul>
            {messages?.map((message:messageType) => (
                <li key={message.id}>
                    <div>
                        <h5>{message.userName}</h5>
                        <p>{message.message}</p>
                    </div>
                </li>
            ))}
        </ul>
    </div>
}
