import React, {ChangeEvent, useState} from "react";
import {Button, Container, Input, useTheme} from "@mui/material";
import SendIcon from '@mui/icons-material/Send';
import {useMutation, useQueryClient} from "react-query";

export function TextBar() {

    const theme = useTheme();

    const [message, setMessage] = useState("");
    const [userName, setUserName] = useState("Admin");

    const sendMessageMutation = useMutation(
        (data: { message: string; userName: string }) => {
            return fetch("http://localhost:8080/chat/messages", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(data)
            }).then((response) => response.json());
        },
        {
            onSuccess: () => {
                console.log("Message sent successfully!");
            },
        }
    )


    const handleSendMessage = () => {
        sendMessageMutation.mutate({message, userName,});
        setMessage("");
        const queryClient = useQueryClient();
        queryClient.invalidateQueries("messages");
    };


    const handleInput = (e: ChangeEvent<HTMLInputElement>) => {
        return setMessage(e.currentTarget.value);
    }


    return <div>
        <Container maxWidth="xl">
            <Input style={{width: "50%", height: "40px"}} onChange={handleInput}/>
            <Button variant="contained" onClick={handleSendMessage}>
                <SendIcon/>
            </Button>
        </Container>
    </div>
}
