import React, {ChangeEvent, useState} from "react";
import { Button, Container, Input, useTheme} from "@mui/material";
import SendIcon from '@mui/icons-material/Send';
import {useQuery} from "react-query";

export function TextBar() {

    const theme = useTheme();

    const [message, setMessage] = useState("");
    const [userName,setuserName] = useState("Admin");

    const sendPostRequest = async (message, userName) => {
        const response = await fetch("http://localhost:8080/messages", {
            method: "POST",
            body: JSON.stringify({ message, userName }),
        });

        if (response.ok) {
            return await response.json();
        } else {
            throw new Error("Ошибка при отправке сообщения");
        }
    };

    const sendMessage = () => {
        useSendMessage(message,userName);
        setMessage("");
        setuserName("");
    }

    const handleSubmit = (e: ChangeEvent<HTMLInputElement>) => {
        return setMessage(e.currentTarget.value);
    }


    return <div>
        <Container maxWidth="xl">
            <Input style={{width: "50%", height: "40px"}} onChange={handleSubmit}/>
            <Button variant="contained" onClick={sendMessage}>
                <SendIcon/>
            </Button>
        </Container>
    </div>
}
