import React from "react";
import {useQuery} from "react-query";
import {List, ListItem, Paper} from "@mui/material/";
import {Avatar, Box, Container, Dialog} from "@mui/material";
import {blueGrey, lightBlue} from "@mui/material/colors";
import {light} from "@mui/material/styles/createPalette";

type messageType = {
    id: string,
    message: string,
    userName: string
    messageTime: string
}

export function MessageList() {
    const fetchMessages = async () => {
        const res = await fetch("http://localhost:8080/chat/messages");
        return res.json();
    }

    const {data: messages, isLoading} = useQuery({
        queryFn: async () => {
            return fetchMessages();

        },
        queryKey: ["data"]
    })
    if (isLoading) return (
        <div>Loading...</div>
    )

    return (

        <div>
            <Container>
                <List sx={{m: 3}}>
                    {messages?.map((message: messageType) => (
                        <ListItem key={message.id}>
                            <Paper elevation={1} sx={{p: 1, display: 'inline-flex', paddingRight: 3, backgroundColor: blueGrey[800]}}>
                                <Avatar sx={{bgcolor: lightBlue [400], maxHeight: 27, maxWidth:27 }}>{message.userName[0]}</Avatar>
                                <Box sx={{marginLeft: 2, color: lightBlue[100]}}>
                                    {message.userName}
                                    <br/>
                                    {message.message}
                                </Box>
                            </Paper>
                        </ListItem>
                    ))}
                </List>
            </Container>
        </div>


        // <div>
        //     <ul>
        //         {messages?.map((message: messageType) => (
        //             <li key={message.id}>
        //                 <div>
        //                     <h5>{message.userName}</h5>
        //                     <p>{message.message}</p>
        //                 </div>
        //             </li>
        //         ))}
        //     </ul>
        // </div>
    )

}
